workflow:
  &CYCLEDEF;
  &LOG_PREFIX;workflow.log






    &EXP_NAME;.fcst.prep
    &LOG_PREFIX;fcst.prep.log
    &fcst.prep.WALLTIME;
    &COM_SCHED_S;



      &SCRIPT_WRAPPER;
      &SCRIPT_DIR;/fcst.prep.sh



    &COM_ENV;

      JOB_WORK_DIR
      &WORK_DIR;/fcst.prep


      ENS_LIST
      &ENS_LIST;









    &ENS_LIST;


      &EXP_NAME;.fcst.run.#mem#
      &LOG_PREFIX_ENS;fcst.run.#mem#.log
      &fcst.run.WALLTIME;
      &SCHED_ACCT;
      &SCHED_P_QUEUE;
      &fcst.run.NODES;:ppn=&PPN;
      &fcst.run.SCHED_ARGS;



        &SCRIPT_WRAPPER;

	# where are the restart files saved?
	export RST_DIR_IN=&ROOT_EXP_DIR;/cycle/$CYCLE/rst/mem_#mem#/
	export RST_DIR_OUT=&ROOT_EXP_DIR;/cycle/$CYCLE_NEXT/rst/mem_#mem#/
	export MEM=#mem#
	# update the restart files if we did DA in previous cycle
	export FCST_RESTART=$([[ $(cat cycle_status) == "ini:&CYCLE;" ]] && echo 0 || echo 1)
	if [[ $FCST_RESTART -eq 1 && ! $DA_MODE == "none" ]]; then 
  	  export JOB_WORK_DIR=&WORK_DIR;/fcst.rst/mem_#mem#
	  time &SCRIPT_DIR;/da.update.sh
	  export RST_DIR_IN=&WORK_DIR;/fcst.rst/mem_#mem#
	fi

	# run the forecast
        &SCRIPT_DIR;/fcst.run.sh

	# this is the end of the cycle, if not doing DA
	if [[ $DA_MODE == "none" ]]; then echo "rst:$CYCLE_NEXT" > $ROOT_EXP_DIR/cycle_status; fi




      &COM_ENV;

	NODES
	&fcst.run.NODES;


	PPN
	&PPN;


	FCST_DIR
	&WORK_DIR;/fcst.run/mem_#mem#


	FORC_DIR
	&WORK_DIR;/fcst.prep/mem_#mem#







	    f = "&ROOT_EXP_DIR;/cycle_status"
	    d=File.open(f,"r").gets.strip[4,10]
	    d == ymdh || d == "&CYCLE;"













    _all 


      &ENS_LIST;


	&EXP_NAME;.da.prep.t#slot#.#mem#
	&LOG_PREFIX_ENS;da.prep.t#slot#.#mem#.log
	&da.prep.WALLTIME;
	&COM_SCHED_S;



	  &SCRIPT_WRAPPER;

	  # combine the forecast daily files and run the obsop on it
	  slots="#slot#"
	  if [[ "$slots" = "_all" ]]; then
	    slots="&DA_WNDW_SLOTS;"; 
	  else
	    exit 1 #TODO, fix this, allow for multiple da.prep jobs, one
                   # for each slot of each member
	  fi    
	  for s in $slots; do
	    export DA_SLOT=$s
	    export OBSOP_FILE=${OBSOP_FILE_TMPL//%slot/$s}
	    &SCRIPT_DIR;/fcst.comb.slot.sh
  	    export JOB_WORK_DIR=&WORK_DIR;/da.prep/omf/work.slots/mem_#mem#/t$s
	    &SCRIPT_DIR;/da.obsop.slot.sh
	  done

	  # combine all the obsop slots into a single file
	  export JOB_WORK_DIR=&WORK_DIR;/da.prep/omf/work.cmb/mem_#mem#
	  export OMF_FILES=&WORK_DIR;/da.prep/omf/mem_#mem#/t*.nc
	  export OUT_FILE=&WORK_DIR;/da.prep/omf/mem_#mem#/obs.nc
	  &SCRIPT_DIR;/da.obsop.comb.sh

	  # only need to combine the restart file if we are actually doing DA
	  if [[ ! $DA_MODE == "none" ]]; then
	    export RST_COMB_DIR=&ROOT_EXP_DIR;/cycle/$CYCLE_NEXT/rst_comb/mem_#mem#/
	    &SCRIPT_DIR;/fcst.comb.rst.sh
	  fi	    



	&COM_ENV;

	  FCST_DIR
	  &WORK_DIR;/fcst.run/mem_#mem#


	  BKG_FILE
	  &WORK_DIR;/da.prep/bkg/mem_#mem#/%Y%m%d.nc


	  OBSOP_FILE_TMPL
	  &WORK_DIR;/da.prep/omf/mem_#mem#/t%slot.nc


	  BKG_RST_DIR
	  &WORK_DIR;/da.prep/bkg_rst/mem_#mem#


	  ANA_RST_DIR
	  &WORK_DIR;/da.prep/ana_rst/mem_#mem#















  ${IF_S_LETKF}


    &EXP_NAME;.da.letkf.run
    &LOG_PREFIX;da.letkf.run.log
    &da.letkf.run.WALLTIME;
    &SCHED_ACCT;
    &SCHED_P_QUEUE;
    &da.letkf.run.NODES;:ppn=&PPN;
    &da.letkf.run.SCHED_ARGS;



      &SCRIPT_WRAPPER;

      export ANA_FILES=&ROOT_EXP_DIR;/cycle/$CYCLE_NEXT/da.letkf/
      &SCRIPT_DIR;/da.letkf.run.sh

      # this is the end of the cycle if only doing LETKF
      if [[ $DA_MODE == "ekf" ]]; then echo "rst:$CYCLE_NEXT" > $ROOT_EXP_DIR/cycle_status; fi



    &COM_ENV;

      TMP_DIR
      &WORK_DIR;/da.letkf


      NODES
      &da.letkf.run.NODES;


      PPN
      &PPN;


      ENS_LIST
      &ENS_LIST;







  ${IF_E_LETKF}





  ${IF_S_HYB}


    &EXP_NAME;.da.prep.3dvar
    &LOG_PREFIX;da.prep.3dvar.log
    &da.prep.3dvar.WALLTIME;
    &COM_SCHED_S;



      &SCRIPT_WRAPPER;

      # combine the forecast daily files and run the obsop on it
      slots="_all"
      if [[ "$slots" = "_all" ]]; then
        slots="&DA_WNDW_SLOTS;";
      else
	exit 1 #TODO, fix this
      fi
      for s in $slots; do
        export DA_SLOT=$s
	export JOB_WORK_DIR=&WORK_DIR;/da.prep.3dvar/omf/work.slots/t$s
	export OBSOP_FILE=${OBSOP_FILE_TMPL//%slot/$s}
	&SCRIPT_DIR;/da.obsop.slot.sh
      done

      # combine all the obsop slots into a single file
      export JOB_WORK_DIR=&WORK_DIR;/da.prep.3dvar/omf/work.cmb/
      export OMF_FILES=&WORK_DIR;/da.prep.3dvar/omf/t*.nc
      export OUT_FILE=&WORK_DIR;/da.prep.3dvar/omf/obs.nc
      &SCRIPT_DIR;/da.obsop.comb.sh



    &COM_ENV;

      BKG_FILE
      &WORK_DIR;/da.letkf/OUTPUT/ana_mean.nc


      OBSOP_FILE_TMPL
      &WORK_DIR;/da.prep.3dvar/omf/t%slot.nc










  ${IF_E_HYB}





  ${IF_S_3DVAR}


    &EXP_NAME;.da.3dvar.run
    &LOG_PREFIX;da.3dvar.run.log
    &da.3dvar.run.WALLTIME;
    &SCHED_ACCT;
    &SCHED_P_QUEUE;
    &da.3dvar.run.NODES;:ppn=&PPN;
    &da.3dvar.run.SCHED_ARGS;



      &SCRIPT_WRAPPER;

      # determine where to pull the obs and bkg from
      if [[ $DA_MODE == "var" ]]; then
        export OBS_FILE=&WORK_DIR;/da.prep/omf/mem_&ENS_LIST;/obs.nc
	export BKG_FILE=&WORK_DIR;/da.prep/bkg/mem_&ENS_LIST;/${CYCLE:0:8}.nc
      else # hyb
        export OBS_FILE=&WORK_DIR;/da.prep.3dvar/omf/obs.nc
	export BKG_FILE=&WORK_DIR;/da.letkf/OUTPUT/ana_mean.nc
      fi      
      export AI_FILE=&ROOT_EXP_DIR;/cycle/$CYCLE_NEXT/da.3dvar/ai.nc

      # run 3DVar
      &SCRIPT_DIR;/da.3dvar.run.sh


      # this is the end of the cycle
      echo "rst:$CYCLE_NEXT" > $ROOT_EXP_DIR/cycle_status




    &COM_ENV;

      NODES
      &da.3dvar.run.NODES;


      PPN
      &PPN;


      TMP_DIR
      &WORK_DIR;/da.3dvar






	${IF_S_3DVAR_HYB}

	${IF_E_3DVAR_HYB}




  ${IF_E_3DVAR}





    &EXP_NAME;.cycle.post

      &SCRIPT_WRAPPER;
      &SCRIPT_DIR;/cycle.post.sh

    &LOG_PREFIX;cycle.post.log
    &da.post.WALLTIME;
    &COM_SCHED_S;

    &COM_ENV;

      JOB_WORK_DIR
      &WORK_DIR;


      ENS_LIST
      &ENS_LIST;





	${IF_S_3DVAR}  ${IF_E_3DVAR}
	${IF_S_LETKF}  ${IF_E_LETKF}
